package io.github.kerubistan.kroki.bytesimport org.junit.jupiter.api.Assertions.assertArrayEqualsimport org.junit.jupiter.api.Assertions.assertEqualsimport org.junit.jupiter.api.Testinternal class BytesKtTest {	@Test	fun toBase64() {		val array = ByteArray(4)		array[0] = 0		array[1] = 1		array[2] = 2		array[3] = 3		assertEquals("AAECAw==", array.toBase64())	}	@Test	fun asBase64() {		assertArrayEquals(			"AAECAw==".asBase64(),			ByteArray(4).apply {				this[0] = 0				this[1] = 1				this[2] = 2				this[3] = 3			}		)	}}